package cz.tokija.tokija;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.view.Menu;import android.view.View;import android.widget.ProgressBar;import android.widget.Toast;import cz.tokija.tokija.client.APIInterface;import cz.tokija.tokija.client.Client;/** * Author: jaroslavmedek on 28/10/2018. */public class BaseActivity extends AppCompatActivity {    private APIInterface client;    public BaseActivity() {        client = new Client().getClient();    }    protected APIInterface getClient(){        return client;    }    protected void gotoLoginIfUnauthorized(int responseCode){        if (responseCode == 401) {            Intent intent = new Intent(BaseActivity.this, LoginActivity.class);            startActivity(intent);        }    }    protected void showToast(String message) {        Toast toast = Toast.makeText(getApplicationContext(),                message,                Toast.LENGTH_LONG);        toast.show();    }}